using System;
using System.Text.RegularExpressions;

namespace RITES_Bill_Saathi.Helpers
{
	public static class HospitalHelper
	{
        public static bool IsHospitalNameInList(string nameToCheck, List<string> listOfNames)
        {
            string normalizedSearchName = NormalizeName(nameToCheck);

            foreach (string hospitalName in listOfNames)
            {
                if (NormalizeName(hospitalName) == normalizedSearchName)
                {
                    return true;
                }
            }

            return false;
        }

        public static string NormalizeName(string name)
        {
            // Remove "M/s", "Pvt. Ltd.", and any non-alphanumeric characters, then make lowercase for case-insensitive comparison
            string normalized = Regex.Replace(name, "M/s |Pvt\\. Ltd\\.|[^a-zA-Z0-9 ]", "", RegexOptions.IgnoreCase);
            return normalized.ToLowerInvariant();
        }


